function copy_dir($src,$dst,$new_date) {
	
    //$new_date = strtotime("2015-07-02 01:09:34");
    $dir = opendir($src);
    @mkdir($dst,0755);
    touch($dst,$new_date);
    while(false !== ( $file = readdir($dir)) ) {
        if (( $file != '.' ) && ( $file != '..' )) {
            if ( is_dir($src . '/' . $file) ) {
                copy_dir($src . '/' . $file,$dst . '/' . $file);
                touch("$dst/$file",$new_date);
                continue;
            }
            else {
                copy($src . '/' . $file,$dst . '/' . $file);
                touch("$dst/$file",$new_date);
            }
        }
    }
    touch($dst,$new_date);
    closedir($dir);
}

